DROP TABLE IF EXISTS `is_sys_perm`;
CREATE TABLE `is_sys_perm`  (
  `perm_id` bigint(20) NOT NULL COMMENT '菜单权限主键',
  `parent_id` bigint(20) NOT NULL COMMENT '所属菜单',
  `name` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '菜单名称',
  `path` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '菜单路径',
  `resource` tinyint(4) NOT NULL DEFAULT 0 COMMENT '菜单类型 （0：菜单 1：按钮 2：表格按钮）',
  `component` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '路由组件',
  `perm` varchar(150) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT 'shiro权限',
  `icon` varchar(150) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '菜单图标',
  `color` varchar(150) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT 'text' COMMENT '按钮颜色',
  `is_cache` tinyint(4) NULL DEFAULT NULL COMMENT '是否缓存 （0：无 1：是）',
  `sort` int(11) NULL DEFAULT 0 COMMENT '排序',
  `link_type` tinyint(4) NULL DEFAULT 0 COMMENT '链接类型 （0：正常 1：满屏 2：外链）',
  `dr` tinyint(4) NULL DEFAULT 1 COMMENT '删除标识 （0：删除 1：正常）',
  `version` int(11) NULL DEFAULT NULL COMMENT '幂等',
  `create_time` datetime(0) NULL DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime(0) NULL DEFAULT NULL COMMENT '更新时间',
  `create_by` varchar(150) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '创建人',
  `update_by` varchar(150) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '更新人',
  PRIMARY KEY (`perm_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '权限表' ROW_FORMAT = Dynamic;

